WITH ins AS (
  INSERT INTO comments (slug, author_name, message, edit_token, is_approved, created_at, updated_at)
  VALUES ('mobilni-web-url', 'ctsixad', 'Přidal bych se k argumentaci pro samostatné adresy pro web a m.web. Pokud se bavíme o „velkých webech“, jak je na začátku článku uvedeno, tak není vhodné zapomínat na nižší vrstvy OSI modelu než je sedmá: oddělení DNS záznamů a výsledně tedy IP webu může mít vliv na TCP optimalizaci pro mobilní sítě, použití rozdílných congestion control algoritmů, záležitostí jako MPTCP (m.web ano, pro plný to nemá smysl), TCP Fast Open apod, což v případě oddělení provozu až na L7 (User-Agent, cookie) není výhodně proveditelné. Ano, velké weby, ne Apache v garáži :)', '6cf39baad930f5f71abda820bb04725accfccf30314c308318d2fafbe0bdcd9a', true, '2015-11-24T21:17:31Z', '2015-11-24T21:17:31Z')
